import React from 'react';
import { Heart } from 'lucide-react';
import api from '../api';

function Comment({ comment, isReply = false, onReply, onUpdate }) {
  // 댓글 좋아요 핸들러 추가
  const handleCommentLike = async (e) => {
    e.stopPropagation(); // 이벤트 버블링 방지

    try {
      await api.request(`/likes/comment/${comment.comment_id}`, { 
        method: 'POST' 
      });

      // 부모 컴포넌트에 업데이트 요청 (게시글 전체 새로고침)
      if (onUpdate) {
        onUpdate();
      }
    } catch (error) {
      console.error('댓글 좋아요 오류:', error);
      alert(error.message);
    }
  };

  return (
    <div className={`${isReply ? 'ml-8 mt-2' : 'mt-4'} p-4 bg-gray-50 rounded-lg`}>
      <div className="flex justify-between items-start mb-2">
        <span className="font-medium">{comment.author}</span>
        <span className="text-xs text-gray-500">
          {new Date(comment.created_time).toLocaleString()}
        </span>
      </div>
      <p className="text-gray-700 mb-2">{comment.content}</p>
      <div className="flex items-center gap-4 text-sm">
        <button 
          onClick={handleCommentLike}
          className="flex items-center gap-1 text-gray-500 hover:text-red-500 transition"
        >
          <Heart className="w-4 h-4" />
          {comment.likes || 0}
        </button>
        {!isReply && onReply && (
          <button
            onClick={() => onReply(comment.comment_id)}
            className="text-blue-500 hover:underline"
          >
            답글
          </button>
        )}
      </div>
      {comment.replies && comment.replies.map(reply => (
        <Comment 
          key={reply.comment_id} 
          comment={reply} 
          isReply={true}
          onUpdate={onUpdate}
        />
      ))}
    </div>
  );
}

export default Comment;
